class appUtilsService {
    constructor() {
    }
    /**
     * returns user nemo/C1 role 
     * @param {string} data Gigya data object
     */
    getC1UserRole(userData) {
        if (userData
            && userData.data
            && userData.data.eduelt
            && userData.data.eduelt.instituteRole
            && userData.data.eduelt.instituteRole.length > 0) {

            const nemoInstitute = userData.data.eduelt.instituteRole.find(institute => institute.platform == 'nemo');
            if (nemoInstitute) return nemoInstitute.role;
        } else if (userData
            && userData.data
            && userData.data.nemo
            && userData.data.nemo.role) {

            return userData.data.nemo.role;
        } else {
            return '';
        }
    }
}

export const appUtils = new appUtilsService();