/* global describe, it, before */

import chai from 'chai';
import { OSDetect } from '../lib';

chai.expect();

const expect = chai.expect;

let os_type;

describe('Given an instance of my OS_DETETCT library', () => {
  before(() => {
    os_type = new OSDetect();
  });
  describe('when I need the name', () => {
    it('should return the name', () => {
      expect(os_type.getOsName).to.be.equal('Windows');
    });
  });
});